export const addNumber = (obj) => 
{
    return{
        type : "ADD_NUMBER",
        payload : obj
    }
}
export const addPlusTwo = () => 
{
    return{
        type : "ADD_TWO"
    }
}
export const removeAll = () => 
{
    return{
        type : "REMOVE_ALL"
    }
}
