const initialState = []

const numberReducer = (state = initialState , action) => 
{
    switch (action.type)
    {
        case "INCREMENT" : {
            return state
        }
        case "INCREMENT_BY" : {
            return state
        }
        default : {
            return [...state]
        }
    }

